const getDateTimeFormatPattern=e=>new Intl.DateTimeFormat(void 0,e).formatToParts(new Date).map((e=>{switch(e.type){case"day":return"d".repeat(e.value.length);case"month":return"M".repeat(e.value.length);case"year":return"y".repeat(e.value.length);case"hour":return"h".repeat(e.value.length);case"minute":return"m".repeat(e.value.length);case"second":return"s".repeat(e.value.length);case"literal":return e.value;default:return""}})).join("");getDateFormat=()=>getDateTimeFormatPattern({dateStyle:"short"}),getMediumDateFormat=()=>getDateTimeFormatPattern({dateStyle:"medium"}),getLongDateFormat=()=>getDateTimeFormatPattern({dateStyle:"long"}),getTimeFormat=()=>getDateTimeFormatPattern({timeStyle:"short"});